import React from 'react';
import EscalaList from './EscalaList';
import './style/SearchBar.css';

class SearchBar extends React.Component {

	getDivStyle = () => {
		return {
			padding: '10px',
			marginLeft: '6.25cm',
			background: '#EEE',

			color: '#1E90FF',
			fontWeight: 'bold',
			fontFamily: 'arial'
		}
	}
	// style={this.getDivStyle()}
	render(){
		const divStyle = {
			padding: '10px',
			marginLeft: '6.25cm',
			background: '#EEE',
		};

		return(
			<div style={divStyle}>
				<p>Usuários</p>
				<input type="text" size="75" placeholder="Pesquise um usuário" />
				<EscalaList />
			</div>
		);
	}

}

export default SearchBar;
