import React from 'react';

class EscalaList extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			escala: [],
			filteredEscala: []
		};
	}

	componentDidMount() {
		this.setState({
			filteredEscala: this.props.escala.data
		})
		console.log("hi");
	}

	renderAllTableRows(){

		/*let filteredEscala = this.props.escala.data.filter(
			function (item, i) {
				return if(this.props.search) item.user === this.props.search
			}
		);*/

		return this.state.filteredEscala.map( (item, i) => {

		//return this.props.escala.data.map((item, i) => {
			return(
				<tr key={i}>
				    <td>{item.user}</td>
				    <td>{item.email}</td>
				    <td>{item.company}</td>
				    <td>{item.escalas}</td>
				    <td>{item.status}</td>
				</tr>
			)
		})
	}

	render(){
		return(
			<div>
			<table>
	  			<thead>
		  			<tr>
		  				<th>Usu√°rio</th>
		  				<th>Email</th>
		  				<th>Company</th>
		  				<th>Escalas</th>
		  				<th>Status</th>
		  			</tr>
	  			</thead>
	  			<tbody>{this.renderAllTableRows()}</tbody>
  			</table>
			</div>
		)
	}

}

export default EscalaList;
