import React from 'react';

class EscalaList extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			escala: [],
			filteredEscala: []
		};
	}

	filterEscala = (myFilter) => {
		let filteredEscala = this.state.escala
		filteredEscala = filteredEscala.filter( (item) => {
			let user = item.user
			return (user.indexOf(myFilter) != -1)
		})
		this.setState({ filteredEscala })
	}

	renderAllTableRows(){
		//return this.props.escala.data.map((item, i) => {
		return this.state.filteredEscala.map((item, i) => {
			return(
				<tr key={i}>
				    <td>{item.user}</td>
				    <td>{item.email}</td>
				    <td>{item.company}</td>
				    <td>{item.escalas}</td>
				    <td>{item.status}</td>
				</tr>
			)
		})
	}

	showResults(){
		if(this.props.search === ""){
			this.renderAllTableRows();
		}
	}

	render(){
		return(
			<div>
			<table>
	  			<thead>
		  			<tr>
		  				<th>Usu√°rio</th>
		  				<th>Email</th>
		  				<th>Company</th>
		  				<th>Escalas</th>
		  				<th>Status</th>
		  			</tr>
	  			</thead>
	  			<tbody>{this.renderAllTableRows()}</tbody>
  			</table>
			</div>
		)
	}

}

export default EscalaList;
