import React from 'react';
import EscalaList from './EscalaList';

class SearchBar extends React.Component {
	constructor(props) {
    	super(props);
    	this.state = {
      		searchItem: ""
    	};
  	}

  	handleChange = (e) => {
		this.setState({ searchItem: e.target.value })
		//this.props.onChange(e.target.value)
	};

	render(){
		const divStyle = {
			padding: '10px',
			marginLeft: '6.25cm',
			background: '#EEE'
		};

		const pStyle = {
			color: '#1E90FF',
			fontWeight: 'bold',
			fontFamily: 'arial'
		};

		const escala = this.props.data;
		const filteredEscala = escala.filter(
			(item) => (item.user !== this.state.search)
		)

		console.log(filteredEscala);
		console.log(filteredEscala.map( (item) => item.user))

		return(
			<div style={divStyle}>
				<p style={pStyle}>Usuários</p>
				<input type="text" size="75" placeholder="Pesquise um usuário" 
					value={this.state.searchItem}
					onChange={this.handleChange} />
				<p></p>
				<EscalaList escala={this.props} search={this.state.searchItem}/>
			</div>
		);
	}

}

export default SearchBar;
