import React from 'react';
import EscalaList from './EscalaList';

class SearchBar extends React.Component {
	constructor(props) {
    	super(props)
    	this.state = {
      		searchItem: ""
    	}
  	}

	render(){
		const divStyle = {
			padding: '10px',
			marginLeft: '6.25cm',
			background: '#EEE'
		};

		const pStyle = {
			color: '#1E90FF',
			fontWeight: 'bold',
			fontFamily: 'arial'
		};

		return(
			<div style={divStyle}>
				<p style={pStyle}>Usuários</p>
				<input type="text" size="75" placeholder="Pesquise um usuário" value={this.state.searchItem}/>
				<p></p>
				<EscalaList escala={this.props} />
			</div>
		);
	}

}

export default SearchBar;
