import React from 'react';

class LoginScreen extends React.Component {

	handleLogin(e){
		e.preventDefault();
		//let username = this.refs.username.value;
		//let password = this.refs.password.value;
	}

	render(){
		const divStyleLogin = {
			margin: '20px',
			borderStyle: 'solid',
			borderWidth: '1px',
			padding: '10px',
			background: '#EEE',
			width: '40%'
		};

		return (
			<div style={divStyleLogin}>
			<h1>Escala Front-End App</h1>
			<form onSubmit={this.handleLogin.bind(this)}>
			<p><b>Usu√°rio: </b><input type="text" ref="username" size="30" placeholder=""/></p>
			<p><b>Senha: </b><input type="password" ref="password" size="30" placeholder=""/></p>
			<p><input type="submit" value="Login"/></p>
			</form>
			</div>
		);
	}
}

class Login extends React.Component {
	constructor(props){
		super(props);

		this.state = {
			escala: [],
			username: [],
			password: "123"
		};

	}

	/* Getting json file from backend application */
	componentDidMount() {
    	fetch('http://homestead.local/escala')
      		.then(response => response.json())
      		.then(escala => this.setState({ escala }))
      		.catch(e => {
        		console.log(e);
        	return e;
    	});

      	///*
      	this.setState({
      		username: this.state.escala
				.map( (item, i) => { return item.user; }),
      	});
      	console.log(this.state.username);
		//*/
  	}

	render(){
		/* Adding the attribute 'removed' to json object before sending it to
	    others componentes */
    	const escala = this.state.escala;
    	escala.map( (item, i) => {
      		return ( item['removed'] = 'false' )
    	});

		return (
			<div>
			<LoginScreen />
			</div>
		)

	}

}

export default Login;
